SELECT a.id AS id, a.auth_code AS authCode, a.auth_text AS authText, a.resoureces_url AS resourecesUrl, a.auth_type AS authType, a.flag AS flag, a.icon AS icon, a.auth_order AS authOrder, a.parent_id AS parentId, a.position as position, p.auth_text AS parentName, a.menu_icon As menuIcon, a.nav_bar_icon As navBarIcon FROM c_authority a LEFT JOIN c_authority p ON a.parent_id = p.id
ORDER BY ${sidx} ${sord}
LIMIT #{startQuery},#{pageSize}

SELECT a.id as id, a.auth_code AS authCode, a.auth_text AS authText, a.resoureces_url AS resourecesUrl FROM c_authority a INNER JOIN c_authority_role z ON a.id = z.auth_id
z.role_id=#{rid}
AND a.auth_type IN
#{type}
AND a.resoureces_url IS NOT NULL

SELECT * FROM ( SELECT DISTINCT a.id as id, a.auth_text AS authText, a.auth_code AS authCode, a.icon AS icon, a.resoureces_url AS resourecesUrl, a.parent_id as parentId, a.auth_order, a.position as position, a.open_way AS openWay, a.menu_icon As menuIcon, a.nav_bar_icon As navBarIcon FROM c_authority a INNER JOIN c_authority_role z ON a.id = z.auth_id
z.role_id IN
#{r}
AND a.parent_id=#{parentId}
AND a.parent_id is null
AND a.auth_type IN
#{type}
AND a.position=#{position}
AND a.flag=#{flag}
)t
ORDER BY t.auth_order ASC

SELECT a.id as id, a.auth_text AS authText, a.auth_type AS authType, a.auth_order AS authOrder, a.position as position, a.parent_id as parentId FROM c_authority a
INNER JOIN c_authority_role z ON a.id = z.auth_id WHERE z.role_id=#{roleId}
ORDER BY a.auth_order ASC


